import randomfrom Deck import *from Hand import *class Card:    def __init__(self):        self.count = 0        self.special = ["wild", "wild four"]        self.colors = ["red","blue","yellow","green"]        self.numbers = ["zero", "one", "two", "three", "four", "five","six","seven", "eight", "nine", "skip", "reverse", "draw two"]        self.cindex = random.randint(0,len(self.colors)-1)        self.rindex = random.randint(0,len(self.numbers)-1)        def getRandomCard(self):        i = random.randint(0,1)        if random.random() <= 0.075:            return self.special[i]        else:            return self.colors[self.cindex], self.numbers[self.rindex]            def getCardsByColor(self, color):        k = 0        c = []        while k < 2:             for i in self.numbers:                temp = color,i                c.append(temp)            k+=1        c.remove(c[0])        return c        def getSpecialCards(self):        k = 0        c = []        while k < 4:            c.append(self.special[0])            c.append(self.special[1])            k+=1        return c