import randomfrom Card import *from Hand import *class Deck:    def __init__(self):        self.cards = []        self.card = Card()        self.deck = self.getDeck()        self.discardPile = []            def getDeck(self):        deck = self.card.getCardsByColor("green")+self.card.getCardsByColor("blue") + self.card.getCardsByColor("red")+self.card.getCardsByColor("yellow") +self.card.getSpecialCards()        return deck        def shuffleDeck(self, deck):        random.shuffle(deck)        self.deck = deck        return deck            def showDeck(self):        print(self.deck)        print()        def showDeckNumber(self):        print(len(self.deck))        print()                    def showDiscardPile(self):        print(self.discardPile)